package ch01.sec09;

public class Date06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//int a = 23.7;
		
		char x;
		
		x = 'A';
		
		System.out.printf(" %c -> %d \n", x, (int)x); //  A -> 65 
		x= '0';
		System.out.printf(" %c -> %d \n", x, (int)x); //  0 -> 48 
		x = 0;
		System.out.printf(" %c -> %d \n", x, (int)x); //     -> 0
	
		x = 'a';
		System.out.printf(" %c -> %d \n", x, (int)x); //   a -> 97

	
	}

}
